import React from "react";
import "./intro.css";
import profile from "../../assets/profile.jpg";   // ✅ correct relative import
import { Link } from "react-scroll";
import btnImg from "../../assets/hireme.png";     // ✅ already correct

function Intro() {
  const OpenResume = () => {
    const resumeURL = process.env.PUBLIC_URL + '/Akash resume.pdf';
    window.open(resumeURL, '_blank');
  };

  return (
    <section id="intro">
      <div className="introContent">
        <span className="hello">Hello,</span>
        <span className="introText">
          I'm <span className="introName">Akash</span>
          <br />
          Website Designer
        </span>
        <p className="introPara">
          I am a skilled Web Developer with experience in creating appealing Web Applications.
        </p>
        <Link>
          <button className="btn" onClick={OpenResume}>
            <img src={btnImg} alt="Hire Me" className="btnImg" />
            Hire Me
          </button>
        </Link>
      </div>

      {/* ✅ use imported variable instead of /profile.jpg */}
      <img src={profile} alt="profile" />
    </section>
  );
}

export default Intro;
